mapper.insert:

	SELECT NVL(MAX(bno), 99) + 1
	FROM employees

INSERT INTO employees(employee_id
	, first_name
	, last_name
	, email
	, phone_number
	, hire_date
	, job_id
	, salary
	, commission_pct
	, manager_id
	, department_id
)
VALUES(#{eid}
	, #{firstName}
	, #{lastName}
	, #{email}
	, #{phoneNumber}
	, #{hireDate}
	, #{jobId}
	, #{salary}
	, #{commissionPct}
	, #{managerId}
	, #{departmentId}
)

mapper.update:
UPDATE employees
SET first_name = #{firstName}
	, last_name = #{lastName}
	, email = #{email}
	, phone_number = #{phoneNumber}
	, hire_date = #{hireDate}
	, job_id = #{jobId}
	, salary = #{salary}
	, commission_pct = #{commissionPct}
	, manager_id = #{managerId}
	, department_id = #{departmentId}
WHERE employee_id = #{employeeId}
